Insert into OE.PRODUCT_DESCRIPTIONS (PRODUCT_ID,LANGUAGE_ID,TRANSLATED_NAME,TRANSLATED_DESCRIPTION) values (1750,'CA','DIMM - 2GB','Memria DIMM: RAM, 2 GB de capacitat.');
Insert into OE.PRODUCT_DESCRIPTIONS (PRODUCT_ID,LANGUAGE_ID,TRANSLATED_NAME,TRANSLATED_DESCRIPTION) values (2394,'CA','DIMM - 32MB','DIMM d''actualitzaci de memria de 32 MB');
Insert into OE.PRODUCT_DESCRIPTIONS (PRODUCT_ID,LANGUAGE_ID,TRANSLATED_NAME,TRANSLATED_DESCRIPTION) values (2400,'CA','DIMM - 512 MB','Memria DIMM de 512 MB. Granularitat d''ampliaci de memria millorada: Calen menys DIMM que SIMM per actualitzar un sistema. Ampliaci dels lmits de memria mxima: amb el mateix nmero de ranures de memria, la quantitat mxima de memria del sistema s superior quan s''utilitzen DIMM que quan s''utilitzen SIMM. Les DIMM tenen contactes separats a cada banda de la placa que dupliquen la relaci de dades d''una SIMM.');
Insert into OE.PRODUCT_DESCRIPTIONS (PRODUCT_ID,LANGUAGE_ID,TRANSLATED_NAME,TRANSLATED_DESCRIPTION) values (1763,'CA','DIMM - 64MB','Memria DIMM: RAM, 64 MB (SDRAM CEE no registrada a 100 MHz)');
