Insert into OE.PRODUCT_DESCRIPTIONS (PRODUCT_ID,LANGUAGE_ID,TRANSLATED_NAME,TRANSLATED_DESCRIPTION) values (3072,'D','HD 12GB /N','12 GB Festplattenlaufwerk fr Narrow Mount. Supra9 Hot-Pluggable Festplattenlaufwerke bieten die Mglichkeit, Laufwerke online zu installieren oder zu entfernen. Sie erfllen strenge Standards fr Verlsslichkeit und Leistung.');
