Insert into OE.PRODUCT_DESCRIPTIONS (PRODUCT_ID,LANGUAGE_ID,TRANSLATED_NAME,TRANSLATED_DESCRIPTION) values (3359,'D','SDRAM - 16 MB','SDRAM Memory-Upgrade-Modul, 16 MB. Synchronous Dynamic Random Access Memory wurde nach EDO eingefhrt. Ermglicht einen schnellen Datenabruf.');
