Insert into OE.PRODUCT_DESCRIPTIONS (PRODUCT_ID,LANGUAGE_ID,TRANSLATED_NAME,TRANSLATED_DESCRIPTION) values (2394,'E','DIMM - 32MB','Actualizacin de Memoria DIMM de 32 MB');
Insert into OE.PRODUCT_DESCRIPTIONS (PRODUCT_ID,LANGUAGE_ID,TRANSLATED_NAME,TRANSLATED_DESCRIPTION) values (2400,'E','DIMM - 512 MB','Memoria DIMM de 512 MB. Granularidad de actualizacin de memoria mejorada: Para actualizar un sistema se necesitan menos DIMM de los que seran necesarios si se utilizaran SIMM en el mismo sistema. Lmites mximos de memoria aumentados: Con un mismo nmero de ranuras de memoria, la memoria mxima de un sistema que utiliza DIMM es mayor que la de uno que utilice SIMM. Las DIMM tienen diferentes contactos en cada lateral de la tarjeta, lo que proporciona un ratio de datos del doble que una SIMM.');
Insert into OE.PRODUCT_DESCRIPTIONS (PRODUCT_ID,LANGUAGE_ID,TRANSLATED_NAME,TRANSLATED_DESCRIPTION) values (1763,'E','DIMM - 64MB','Memoria DIMM: 64 MB de RAM (SDRAM ECC no Registrada de 100 MHz)');
Insert into OE.PRODUCT_DESCRIPTIONS (PRODUCT_ID,LANGUAGE_ID,TRANSLATED_NAME,TRANSLATED_DESCRIPTION) values (2396,'E','EDO - 32MB','Memoria SIM EDO: 32 MB de RAM (SDRAM ECC no Registrada de 100 MHz). Al igual que FPM, EDO est disponible en SIMM y DIMM, en 3,3 y 5 voltios. Si la memoria EDO est instalada en una computadora que no est diseada para soportarla, puede que la memoria no funcione.');
Insert into OE.PRODUCT_DESCRIPTIONS (PRODUCT_ID,LANGUAGE_ID,TRANSLATED_NAME,TRANSLATED_DESCRIPTION) values (2272,'E','RAM - 16 MB','Memoria SIMM: RAM de 16 MB de capacidad.');
