Insert into OE.PRODUCT_DESCRIPTIONS (PRODUCT_ID,LANGUAGE_ID,TRANSLATED_NAME,TRANSLATED_DESCRIPTION) values (1782,'PTB','Compact 400/DQ','Impressora de qualidade de carta de alta velocidade (400 caracteres por segundo). Dimenses (AxLxP): 12,37 x 27,96 x 23,92 polegadas. Interface: RS-232 serial (25 pinos), 3 slots de expanso. Tamanho do papel: A2, A3, A4.');
Insert into OE.PRODUCT_DESCRIPTIONS (PRODUCT_ID,LANGUAGE_ID,TRANSLATED_NAME,TRANSLATED_DESCRIPTION) values (3069,'PTB','HD 10GB /S','Unidade de disco rgido de 10GB para montagem Padro. Compatibilidade retroativa com sistemas Supra5, os usurios tm a liberdade de distribuir e redistribuir essas unidades para obter maior capacidade de armazenamento rapidamente. As unidades Supra eliminam o risco de incompatibilidade de firmware.');
