Insert into OE.PRODUCT_DESCRIPTIONS (PRODUCT_ID,LANGUAGE_ID,TRANSLATED_NAME,TRANSLATED_DESCRIPTION) values (3354,'PTB','HD 12GB /I','Unidade de disco rgido de 12GB (interna). As unidades Supra eliminam o risco de incompatibilidade de firmware. Compatibilidade retroativa: voc pode misturar ou combinar os dispositivos Supra2 e Supra3 para obter solues otimizadas e expanso futura.');
Insert into OE.PRODUCT_DESCRIPTIONS (PRODUCT_ID,LANGUAGE_ID,TRANSLATED_NAME,TRANSLATED_DESCRIPTION) values (3072,'PTB','HD 12GB /N','Unidade de disco rgido de 12GB para Montagem Restrita. As unidades de disco rgido Supra9 de conexo direta oferecem a capacidade de instalao ou remoo das unidades on-line. Nossas unidades de disco rgido de conexo direta so necessrias para atender aos nossos rigorosos padres de confiabilidade e desempenho.');
Insert into OE.PRODUCT_DESCRIPTIONS (PRODUCT_ID,LANGUAGE_ID,TRANSLATED_NAME,TRANSLATED_DESCRIPTION) values (3334,'PTB','HD 12GB /R','Unidade de disco rgido removvel de 12GB. Com a compatibilidade entre diversas plataformas empresariais, voc ter a liberdade de distribuir e redistribuir essa unidade para oferecer rapidamente maior capacidade de armazenamento. As unidades de disco rgido Supra7 Universal constituem a segunda gerao de unidades de conexo direta de alta performance, que compartilham compatibilidade entre os servidores corporativos e incluses de armazenamento externo.');
Insert into OE.PRODUCT_DESCRIPTIONS (PRODUCT_ID,LANGUAGE_ID,TRANSLATED_NAME,TRANSLATED_DESCRIPTION) values (3071,'PTB','HD 12GB /S','Unidade de disco rgido de 12GB para Montagem Padro. As unidades de disco rgido de conexo direta Supra9 oferecem a capacidade de instalao ou remoo das unidades on-line. Nossas unidades de disco rgido de conexo direta so necessrias para atender aos nossos rigorosos padres de confiabilidade e desempenho.');
Insert into OE.PRODUCT_DESCRIPTIONS (PRODUCT_ID,LANGUAGE_ID,TRANSLATED_NAME,TRANSLATED_DESCRIPTION) values (2255,'PTB','HD 12GB @7200 /SE','Unidade de disco rgido de 12GB (externa) SCSI, 7200 RPM. Essas unidades destinam-se ao uso nos ambientes crticos e exigentes de hoje e podem ser utilizadas em aplicaes RAID. Kits de opes universais esto configurados e previamente montados na bandeja de conexo direta para instalao imediata no seu servidor corporativo ou sistema de armazenamento.');
Insert into OE.PRODUCT_DESCRIPTIONS (PRODUCT_ID,LANGUAGE_ID,TRANSLATED_NAME,TRANSLATED_DESCRIPTION) values (1743,'PTB','HD 18.2GB @10000 /E','Unidade de disco rgido externa - 18,2 GB, com at 10.000 RPM. Essas unidades destinam-se ao uso nos ambientes crticos e exigentes de hoje e so ideais para utilizao em aplicaes RAID.');
