Insert into OE.PRODUCT_DESCRIPTIONS (PRODUCT_ID,LANGUAGE_ID,TRANSLATED_NAME,TRANSLATED_DESCRIPTION) values (3399,'PTB','HD 18GB /SE','Unidade de disco rgido 18GB SCSI externa. As unidades de disco rgido Supra5 Universal oferecem a capacidade de conexo direta entre vrios servidores, arrays de RAID e itens de armazenamento externo.');
