Insert into OE.PRODUCT_DESCRIPTIONS (PRODUCT_ID,LANGUAGE_ID,TRANSLATED_NAME,TRANSLATED_DESCRIPTION) values (2400,'PL','DIMM - 512 MB','Pami 512 MB DIMM. ?atwiejsza rozbudowa pamici: Do uaktualnienia systemu potrzeba mniej ukadw DIMM ni SIMM. Ponadto ukady DIMM maj dwa rzdy stykw, dziki czemu s dwukrotnie wydajniejsze od ukadw SIMM.');
Insert into OE.PRODUCT_DESCRIPTIONS (PRODUCT_ID,LANGUAGE_ID,TRANSLATED_NAME,TRANSLATED_DESCRIPTION) values (1763,'PL','DIMM - 64MB','Pami DIMM: RAM, 64 MB (100-MHz, niezarejestrowane ECC SDRAM)');
Insert into OE.PRODUCT_DESCRIPTIONS (PRODUCT_ID,LANGUAGE_ID,TRANSLATED_NAME,TRANSLATED_DESCRIPTION) values (2396,'PL','EDO - 32MB','Pami EDO SIM: RAM, 32 MB (100-MHz, niezarejestrowane ECC SDRAM). Podobnie do ukadw FPM, EDO jest dostpne jako ukady SIMM i DIMM, w odmianach 3,3- oraz 5-woltowych. Pami EDO wymaga specjalnie zaprojektowanej pyty gwnej komputera.');
Insert into OE.PRODUCT_DESCRIPTIONS (PRODUCT_ID,LANGUAGE_ID,TRANSLATED_NAME,TRANSLATED_DESCRIPTION) values (2272,'PL','RAM - 16 MB','Pami SIMM: RAM - 16 MB.');
