Insert into OE.PRODUCT_DESCRIPTIONS (PRODUCT_ID,LANGUAGE_ID,TRANSLATED_NAME,TRANSLATED_DESCRIPTION) values (3224,'N','Kortorganisator - 250','Brbar holder for organisering av visittkort, kapasitet p 250. Heftestil med gjennomsiktige lommer for visittkort. Valgfrie alfabetryttere. Angi dekselfarge ved bestilling (mrk brun, burgunder, svart og lys gr).');
Insert into OE.PRODUCT_DESCRIPTIONS (PRODUCT_ID,LANGUAGE_ID,TRANSLATED_NAME,TRANSLATED_DESCRIPTION) values (3225,'N','Kortorganisator - 1000','Holder for organisering av visittkort med alfabetisk inndeling, kapasitet p 1000. Bordmodell med rkgrtt deksel og svart sokkel. Lokket kan fjernes for lagring i en skuff.');
Insert into OE.PRODUCT_DESCRIPTIONS (PRODUCT_ID,LANGUAGE_ID,TRANSLATED_NAME,TRANSLATED_DESCRIPTION) values (3511,'N','Papir - Hykvalitetsskriver','Kvalitetspapir for blekk- og laserskrivere, testet for  unng papirstopp. Syrefritt for arkiveringsforml. 22-punds vekt med lysstyrke p 92. Strrelse: 8 1/2 x 11. Enkle 500-arks bunker.');
