Insert into OE.PRODUCT_DESCRIPTIONS (PRODUCT_ID,LANGUAGE_ID,TRANSLATED_NAME,TRANSLATED_DESCRIPTION) values (2596,'N','SS-stokk - 1 mm','Stokk i rustfritt stl - 3 mm. Kan forhndsdrilles for standardmodell av hovedkort og batteriholdere. Bruk den riktige malen for  identifisere modellnummer, plassering og strrelse for ferdig ark ved bestilling av drillet ark.');
Insert into OE.PRODUCT_DESCRIPTIONS (PRODUCT_ID,LANGUAGE_ID,TRANSLATED_NAME,TRANSLATED_DESCRIPTION) values (2631,'N','ESD-bnd/QR','Elektrostatisk avledningsbnd: 2 deler med enkel frigjringskontakt. Den ene delen er permanent montert p datamaskinen med en skru, den andre er knyttet til bndet. Ekstra permanente ender er tilgjengelig.');
Insert into OE.PRODUCT_DESCRIPTIONS (PRODUCT_ID,LANGUAGE_ID,TRANSLATED_NAME,TRANSLATED_DESCRIPTION) values (2721,'N','PC-veske - L/S','Breveske for datamaskin i svart lr - kapasitet p en enkelt laptop-maskin med lommer for hndbker, ekstra maskinvare og arbeidspapirer. Justerbare beskyttende stropper og ls lomme for strmforsyningsenhet og kabler.');
Insert into OE.PRODUCT_DESCRIPTIONS (PRODUCT_ID,LANGUAGE_ID,TRANSLATED_NAME,TRANSLATED_DESCRIPTION) values (2722,'N','PC-veske - L/D','Breveske for datamaskin i svart lr - kapasitet p to laptop-maskiner med lommer for ekstra maskinvare eller hndbker og arbeidspapirer. Justerbare beskyttende stropper og ls lomme for strmforsyningsenhet og kabler. Bred skulderstropp for ekstra komfort.');
Insert into OE.PRODUCT_DESCRIPTIONS (PRODUCT_ID,LANGUAGE_ID,TRANSLATED_NAME,TRANSLATED_DESCRIPTION) values (2725,'N','Maskinolje','Maskinolje for smring av CD-ROM-stasjondrer og -beskyttere. Selvrensende justerbar dyse for fin til medium flyt.');
