Insert into OE.PRODUCT_DESCRIPTIONS (PRODUCT_ID,LANGUAGE_ID,TRANSLATED_NAME,TRANSLATED_DESCRIPTION) values (2359,'PL','Monitor LCD 9/PM','9-calowy pasywny monitor z wywietlaczem ciekokrystalicznym. Niewielkie wymiary monitora pozwalaj zaoszczdzi wiele cennego miejsca na biurku. ?atwa konfiguracja dziki zgodnoci ze standardem "plug and play".');
