Insert into OE.PRODUCT_DESCRIPTIONS (PRODUCT_ID,LANGUAGE_ID,TRANSLATED_NAME,TRANSLATED_DESCRIPTION) values (2721,'PL','Torba na PC - L/S','Czarna torba skrzana na laptop - kieszenie na podrczniki, papier i dodatkowe urzdzenia peryferyjne. Regulowane pasy i odpinana kiesze na zasilacz i przewody.');
Insert into OE.PRODUCT_DESCRIPTIONS (PRODUCT_ID,LANGUAGE_ID,TRANSLATED_NAME,TRANSLATED_DESCRIPTION) values (2722,'PL','Torba na PC - L/D','Czarna torba skrzana na laptop - mieci dwa laptopy; kieszenie na podrczniki, papier i dodatkowe urzdzenia peryferyjne. Regulowane, wygodne pasy o podwjnej szerokoci i odpinana kiesze na zasilacz i przewody.');
