Insert into OE.PRODUCT_DESCRIPTIONS (PRODUCT_ID,LANGUAGE_ID,TRANSLATED_NAME,TRANSLATED_DESCRIPTION) values (3400,'PTB','HD 8GB /SE','Unidade de disco rgido de 8GB SCSI (externa). As unidades de disco Supra7 oferecem a tecnologia mais recente para melhorar o desempenho empresarial, aumentando a taxa mxima de transferncia de dados em at 255MB/s.');
