Insert into OE.PRODUCT_DESCRIPTIONS (PRODUCT_ID,LANGUAGE_ID,TRANSLATED_NAME,TRANSLATED_DESCRIPTION) values (2722,'D','PC Bag - L/D','Schwarze Lederhülle für Computer - für zwei Laptops, einschließlich Handbüchern, zusätzlicher Hardware und Arbeitspapieren. Doppelt breiter Tragegurt. Abnehmbare Tasche für Netzteil und Kabel. Tragegurt mit doppelter Breite.');
