Insert into OE.PRODUCT_DESCRIPTIONS (PRODUCT_ID,LANGUAGE_ID,TRANSLATED_NAME,TRANSLATED_DESCRIPTION) values (2236,'DK','Plasmaskærm 10/TFT/XGA','10" TFT XGA flatscreen-skærm til bærbare computere');
Insert into OE.PRODUCT_DESCRIPTIONS (PRODUCT_ID,LANGUAGE_ID,TRANSLATED_NAME,TRANSLATED_DESCRIPTION) values (3054,'DK','Plasmaskærm 10/XGA','10" standardplasmaskærm, XGA-opløsning. Denne næsten flade skærm med høj opløsning leverer fremragende billedkvalitet med reduceret genskin.');
Insert into OE.PRODUCT_DESCRIPTIONS (PRODUCT_ID,LANGUAGE_ID,TRANSLATED_NAME,TRANSLATED_DESCRIPTION) values (1782,'DK','Kompakt 400/DQ','Højhastighedsprinter til kladder, 400 tegn pr. sek. Dimensioner (HxBxD): 17,34 x 24,26 x 26,32 tommer. Interface: RS-232 seriel (9-pin), ingen udvidelsessokler. Papirformat: A4, DA Letter.');
Insert into OE.PRODUCT_DESCRIPTIONS (PRODUCT_ID,LANGUAGE_ID,TRANSLATED_NAME,TRANSLATED_DESCRIPTION) values (2430,'DK','Kompakt 400/LQ','Højhastighedsprinter i brevkvalitet, 400 tegn pr. sek. Dimensioner (HxBxD): 12,37 x 27,96 x 23,92 tommer. Interface: RS-232 seriel (25-pin), 3 udvidelsessokler. Papirformat: A2, A3, A4.');
Insert into OE.PRODUCT_DESCRIPTIONS (PRODUCT_ID,LANGUAGE_ID,TRANSLATED_NAME,TRANSLATED_DESCRIPTION) values (1792,'DK','Industriel 600/DQ','Højhastighedsprinter til kladder, bredformat, farveegenskaber, 600 tegn pr. sek. Dimensioner (HxBxD): 22,31 x 25,73 x 20,12 tommer. Papirformat: 3x5 tommer til 11x17 tommer fuldfarvet bredformat.');
Insert into OE.PRODUCT_DESCRIPTIONS (PRODUCT_ID,LANGUAGE_ID,TRANSLATED_NAME,TRANSLATED_DESCRIPTION) values (1791,'DK','Industriel 700/HD','Matrix-printer, 700 tegn pr. sek., kraftig konstruktion og med støvbeskyttelse til industrielle formål. Interface: Centronics parallel, IEEE 1284-kompatibel. Papirformat: 3x5 tommer til 11x17 tommer fuldfarvet bredformat. Hukommelse: 4MB. Dimensioner (HxBxD): 9,3 x 16,5 x 13 tommer.');
Insert into OE.PRODUCT_DESCRIPTIONS (PRODUCT_ID,LANGUAGE_ID,TRANSLATED_NAME,TRANSLATED_DESCRIPTION) values (2302,'DK','Inkjet B/6','Inkjet-printer, sort/hvid, 6 sider pr. minut, opløsning 600x300 dpi. Interface: Centronics parallel, IEEE 1284-kompatibel. Dimensioner (HxBxD): 7,3 x 17,5 x 14 tommer. Papirformat: A3, A4, DA legal. Ingen udvidelsessokler.');
Insert into OE.PRODUCT_DESCRIPTIONS (PRODUCT_ID,LANGUAGE_ID,TRANSLATED_NAME,TRANSLATED_DESCRIPTION) values (2453,'DK','Inkjet C/4','Inkjet-printer, farve (med to separate blækpatroner), 6 sider pr. minut i sort/hvid, 4 sider pr. minut i farve, opløsning 600x300 dpi. Interface: Tovejs IEEE 1284-kompatibelt parallelinterface og RS-232 serielt (9-pin) interface, 2 åbne EIO-udvidelsessokler. Hukommelse: 8MB, 96KB modtagerbuffer.');
Insert into OE.PRODUCT_DESCRIPTIONS (PRODUCT_ID,LANGUAGE_ID,TRANSLATED_NAME,TRANSLATED_DESCRIPTION) values (1797,'DK','Inkjet C/8/HQ','Inkjet-printer, farve, 8 sider pr. minut, høj opløsning (fotokvalitet). Hukommelse: 16MB. Dimensioner (HxBxD): 7.3 x 17.5 x 14 tommer. Papirformat: A4, DA Letter, konvolutter. Interface: Centronics parallel, IEEE 1284-kompatibel.');
Insert into OE.PRODUCT_DESCRIPTIONS (PRODUCT_ID,LANGUAGE_ID,TRANSLATED_NAME,TRANSLATED_DESCRIPTION) values (2459,'DK','LaserPro 1200/8/BW','Professionel sort/hvid laserprinter, opløsning 1200 dpi, 8 sider pr. sek. Dimensioner (HxBxD): 22,37 x 19,86 x 21,92 tommer. Software: Forbedret driverunderstøttelse for SPNIX v4.0; MS-DOS Indbyggede printerdrivere: ZoomSmart scaling-teknologi, plakat, pressemeddelelse, spejl, vandmærke, vis udskrift, hurtigsæt, emulering af laserprintermargener.');
Insert into OE.PRODUCT_DESCRIPTIONS (PRODUCT_ID,LANGUAGE_ID,TRANSLATED_NAME,TRANSLATED_DESCRIPTION) values (3127,'DK','LaserPro 600/6/BW','Sort/hvid standardlaserprinter, opløsning 600 dpi, 6 sider pr. sek. Interface: Centronics parallel, IEEE 1284-kompatibel. Hukommelse: 8MB, 96KB modtagerbuffer. MS-DOS værktøjsprogrammer til SPNIX AutoCAM v.17-kompatibel driver.');
Insert into OE.PRODUCT_DESCRIPTIONS (PRODUCT_ID,LANGUAGE_ID,TRANSLATED_NAME,TRANSLATED_DESCRIPTION) values (2254,'DK','HD 10GB /I','10GB harddisk (intern). Disse diske er beregnet til brug i nutidens krævende, datakritiske miljøer og er ideelle til brug i RAID-applikationer. Universelle optionskit er konfigureret og monteret på forhånd i den rigtige hot plug-skuffe, så disken straks kan installeres i virksomhedens server eller lagersystem.');
Insert into OE.PRODUCT_DESCRIPTIONS (PRODUCT_ID,LANGUAGE_ID,TRANSLATED_NAME,TRANSLATED_DESCRIPTION) values (3353,'DK','HD 10GB /R','10GB flytbar harddisk. Supra7-diske leverer den seneste teknologi til forbedring af virksomhedens effektivitet ved forøgelse af den maksimale dataoverførselshastighed til 160MB/s.');
Insert into OE.PRODUCT_DESCRIPTIONS (PRODUCT_ID,LANGUAGE_ID,TRANSLATED_NAME,TRANSLATED_DESCRIPTION) values (3069,'DK','HD 10GB /S','10GB harddisk til standardmontering. Bagudkompatibel med Supra5-systemer, så brugere kan koble disse diske til og fra efter behov og derved hurtigt forøge lagerkapaciteten. Supra-diske fjerner risikoen for inkompatibel firmware.');
Insert into OE.PRODUCT_DESCRIPTIONS (PRODUCT_ID,LANGUAGE_ID,TRANSLATED_NAME,TRANSLATED_DESCRIPTION) values (2253,'DK','HD 10GB @5400 /SE','10GB harddisk (ekstern) SCSI-interface, 5400 RPM. Universelle optionskit er konfigureret og monteret på forhånd i den rigtige hot plug-skuffe, så disken straks kan installeres i virksomhedens server eller lagersystem. Supra-diske fjerner risikoen for inkompatibel firmware.');
Insert into OE.PRODUCT_DESCRIPTIONS (PRODUCT_ID,LANGUAGE_ID,TRANSLATED_NAME,TRANSLATED_DESCRIPTION) values (3354,'DK','HD 12GB /I','12GB harddisk (intern). Supra-diske fjerner risikoen for inkompatibel firmware. Bagudkompatibilitet: Du kan blande Supra2- og Supra3-enheder for at opnå optimale løsninger og fremtidig vækst.');
Insert into OE.PRODUCT_DESCRIPTIONS (PRODUCT_ID,LANGUAGE_ID,TRANSLATED_NAME,TRANSLATED_DESCRIPTION) values (3072,'DK','HD 12GB /N','12GB harddisk til Narrow Mount. Supra9 hot plug-harddiske giver mulighed for at installere eller fjerne diske online. Hot plug-harddiskene skal overholde vores skrappe krav til pålidelighed og ydeevne.');
Insert into OE.PRODUCT_DESCRIPTIONS (PRODUCT_ID,LANGUAGE_ID,TRANSLATED_NAME,TRANSLATED_DESCRIPTION) values (3334,'DK','HD 12GB /R','12GB flytbar harddisk. Med kompatibilitet på tværs af mange virksomhedsplatforme kan du koble denne disk til og fra efter behov for hurtigt at levere øget lagerkapacitet. Supra7 Universal-diske er anden generation af højeffektive hot plug-diske, der deler kompatibilitet på tværs af virksomhedsservere og eksterne lageraftaler.');
Insert into OE.PRODUCT_DESCRIPTIONS (PRODUCT_ID,LANGUAGE_ID,TRANSLATED_NAME,TRANSLATED_DESCRIPTION) values (3071,'DK','HD 12GB /S','12GB harddisk til standardmontering. Supra9 hot plug-harddiske giver mulighed for at installere eller fjerne diske online. Hot plug-harddiskene skal overholde vores skrappe krav til pålidelighed og ydeevne.');
Insert into OE.PRODUCT_DESCRIPTIONS (PRODUCT_ID,LANGUAGE_ID,TRANSLATED_NAME,TRANSLATED_DESCRIPTION) values (2255,'DK','HD 12GB @7200 /SE','12GB harddisk (ekstern) SCSI, 7200 RPM. Disse diske er beregnet til brug i nutidens krævende, datakritiske miljøer og er ideelle til brug i RAID-applikationer. Universelle optionskit er konfigureret og monteret på forhånd i den rigtige hot plug-skuffe, så disken straks kan installeres i virksomhedens server eller lagersystem.');
Insert into OE.PRODUCT_DESCRIPTIONS (PRODUCT_ID,LANGUAGE_ID,TRANSLATED_NAME,TRANSLATED_DESCRIPTION) values (1743,'DK','HD 18.2GB @10000 /E','Ekstern harddisk - 18,2 GB, med hastigheder på op til 10,000 RPM. Disse diske er beregnet til brug i nutidens krævende, datakritiske miljøer og er ideelle til brug i RAID-applikationer.');
Insert into OE.PRODUCT_DESCRIPTIONS (PRODUCT_ID,LANGUAGE_ID,TRANSLATED_NAME,TRANSLATED_DESCRIPTION) values (2382,'DK','HD 18.2GB@10000 /I','18,2 GB SCSI-harddisk @ 10000 RPM (intern). Supra7 Universal-diske giver en uovertruffen beskyttelse af investeringen og forenkling for kunderne ved at muliggøre diskkompatibilitet på tværs af mange virksomhedsplatforme.');
Insert into OE.PRODUCT_DESCRIPTIONS (PRODUCT_ID,LANGUAGE_ID,TRANSLATED_NAME,TRANSLATED_DESCRIPTION) values (3399,'DK','HD 18GB /SE','18GB SCSI ekstern harddisk. Supra5 Universal-harddiske giver mulighed for hot plug mellem forskellig servere, RAIDs og eksterne lagre.');
Insert into OE.PRODUCT_DESCRIPTIONS (PRODUCT_ID,LANGUAGE_ID,TRANSLATED_NAME,TRANSLATED_DESCRIPTION) values (3073,'DK','HD 6GB /I','6GB harddisk (intern). Supra-diske fjerner risikoen for inkompatibel firmware.');
Insert into OE.PRODUCT_DESCRIPTIONS (PRODUCT_ID,LANGUAGE_ID,TRANSLATED_NAME,TRANSLATED_DESCRIPTION) values (1768,'DK','HD 8.2GB @5400','Harddisk - 8,2 GB, med hastigheder på op til 5.400 RPM. Supra-diske fjerner risikoen for inkompatibel firmware. Serielt standard-RS-232-interface.');
Insert into OE.PRODUCT_DESCRIPTIONS (PRODUCT_ID,LANGUAGE_ID,TRANSLATED_NAME,TRANSLATED_DESCRIPTION) values (2410,'DK','HD 8.4GB @5400','8,4 GB harddisk @ 5400 RPM. Reducerede omkostninger: Diskene kan bruges på tværs af virksomhedsplatforme. Denne hot plug-harddisk skal overholde dine skrappe krav til pålidelighed og ydeevne.');
Insert into OE.PRODUCT_DESCRIPTIONS (PRODUCT_ID,LANGUAGE_ID,TRANSLATED_NAME,TRANSLATED_DESCRIPTION) values (2257,'DK','HD 8GB /I','8GB harddisk (intern). Supra9 hot plug-harddiske giver mulighed for at installere eller fjerne diske online. Bagudkompatibilitet: Du kan blande Supra2- og Supra3-enheder for at opnå optimale løsninger og fremtidig vækst.');
Insert into OE.PRODUCT_DESCRIPTIONS (PRODUCT_ID,LANGUAGE_ID,TRANSLATED_NAME,TRANSLATED_DESCRIPTION) values (3400,'DK','HD 8GB /SE','8GB SCSI-harddisk (ekstern). Supra7-diske leverer den seneste teknologi til forbedring af virksomhedens effektivitet ved forøgelse af den maksimale dataoverførselshastighed til 255MB/s.');
Insert into OE.PRODUCT_DESCRIPTIONS (PRODUCT_ID,LANGUAGE_ID,TRANSLATED_NAME,TRANSLATED_DESCRIPTION) values (3355,'DK','HD 8GB /SI','8GB SCSI-harddisk, intern. Med kompatibilitet på tværs af mange virksomhedsplatforme kan du koble denne disk til og fra efter behov for hurtigt at levere øget lagerkapacitet.');
Insert into OE.PRODUCT_DESCRIPTIONS (PRODUCT_ID,LANGUAGE_ID,TRANSLATED_NAME,TRANSLATED_DESCRIPTION) values (1772,'DK','HD 9.1GB @10000','Harddisk - 9,1 GB, med hastigheder på op til 10.000 RPM. Disse diske er beregnet til brug i datakritiske virksomhedsmiljø. Gør arbejdet nemmere: Du kan let vælge de diske, som du har behov for, uafhængigt af hvad de skal anvendes til.');
Insert into OE.PRODUCT_DESCRIPTIONS (PRODUCT_ID,LANGUAGE_ID,TRANSLATED_NAME,TRANSLATED_DESCRIPTION) values (2414,'DK','HD 9.1GB @10000 /I','9,1 GB SCSI-harddisk @ 10000 RPM (intern). Supra7-diske kan fås med hastigheder på 10.000 RPM og kapaciteter på 18GB og 9,1 GB. SCSI- og RS-232-interfaces.');
Insert into OE.PRODUCT_DESCRIPTIONS (PRODUCT_ID,LANGUAGE_ID,TRANSLATED_NAME,TRANSLATED_DESCRIPTION) values (2415,'DK','HD 9.1GB @7200','9,1 GB harddisk @ 7200 RPM. Universelle optionskit er konfigureret og monteret på forhånd i den rigtige hot plug-skuffe, så disken straks kan installeres i virksomhedens server eller lagersystem.');
