Insert into OE.PRODUCT_DESCRIPTIONS (PRODUCT_ID,LANGUAGE_ID,TRANSLATED_NAME,TRANSLATED_DESCRIPTION) values (2410,'E','HD 8.4GB @5400','Disco duro de 8,4 GB @ 5400 RPM. Costo de adquisición reducido: Las unidades se pueden utilizar en plataformas de empresa. Esta unidad de disco duro de conexión directa debe cumplir los rigurosos estándares de proporcionar fiabilidad y rendimiento.');
