Insert into OE.PRODUCT_DESCRIPTIONS (PRODUCT_ID,LANGUAGE_ID,TRANSLATED_NAME,TRANSLATED_DESCRIPTION) values (3334,'E','HD 12GB /R','Disco duro extraíble de 12 GB. Compatible con un gran número de plataformas de empresa, puede ampliar y volver a ampliar estas unidades para obtener rápidamente una mayor capacidad de almacenamiento. Las unidades de disco universales Supra7 son la segunda generación de unidades de entrada directa de alto rendimiento que comparten compatibilidad en servidores corporativos y dispositivos adjuntos de almacenamiento externo.');
