Insert into OE.PRODUCT_DESCRIPTIONS (PRODUCT_ID,LANGUAGE_ID,TRANSLATED_NAME,TRANSLATED_DESCRIPTION) values (2336,'ESA','Caja de Tarjetas de Presentación - 250','Caja de tarjetas de presentación, con capacidad para 250 tarjetas. Utilice el formulario BC110-2, Rev. 3/2000 (copia impresa o en pantalla) al realizar el pedido y rellene todos los campos marcados con un asterisco.');
