Insert into OE.PRODUCT_DESCRIPTIONS (PRODUCT_ID,LANGUAGE_ID,TRANSLATED_NAME,TRANSLATED_DESCRIPTION) values (3209,'ESA','Sacapuntas - Lápiz','Cortadores de acero afilados del sacapuntas eléctrico de larga duración. PencilSaver ayuda a evitar que se afile demasiado. Pie de caucho no deslizante. Contenedor de lápices incorporado.');
Insert into OE.PRODUCT_DESCRIPTIONS (PRODUCT_ID,LANGUAGE_ID,TRANSLATED_NAME,TRANSLATED_DESCRIPTION) values (3224,'ESA','Clasificador de Tarjetas - 250','Contenedor portátil para organizar las tarjetas de presentación, con capacidad para 250 tarjetas. Estilo folleto con bolsillos deslizantes y transparentes para tarjetas de presentación. Lengüetas alfabéticas opcionales. Especifique el color de la cubierta al realizar el pedido (marrón oscuro, beige, borgoña, negro y gris claro).');
