Insert into OE.PRODUCT_DESCRIPTIONS (PRODUCT_ID,LANGUAGE_ID,TRANSLATED_NAME,TRANSLATED_DESCRIPTION) values (3511,'ESA','Papel - Impresora HQ','Papel de calidad para impresoras de inyección de tinta y láser resistente a atascos de la impresora. Sin ácido para objetivos de archivado. 82,5 gm/m2 (22 libras) con un brillo de 92. Tamaño: 21,6 x 27,9 cm (8,5 x 11 pulgadas). Paquetes individuales de 500 hojas.');
