Insert into OE.PRODUCT_DESCRIPTIONS (PRODUCT_ID,LANGUAGE_ID,TRANSLATED_NAME,TRANSLATED_DESCRIPTION) values (1940,'ESA','Brazalete/Clip ESD','Brazalete de descarga electrostática con clip de pinza para una conexión sencilla al chasis de la computadora u otra conexión.');
Insert into OE.PRODUCT_DESCRIPTIONS (PRODUCT_ID,LANGUAGE_ID,TRANSLATED_NAME,TRANSLATED_DESCRIPTION) values (2030,'ESA','Guantes de Látex','Guantes de látex para ensambladores, manipuladores de productos químicos y ajustadores. Resistente, nivel de seguridad naranja, con textura de refuerzo en los dedos. Impermeables y a prueba de descargas de hasta 220 V/2 amperios, 110 V/5 amperios. Resistente al ácido hasta durante 5 minutos.');
