Insert into OE.PRODUCT_DESCRIPTIONS (PRODUCT_ID,LANGUAGE_ID,TRANSLATED_NAME,TRANSLATED_DESCRIPTION) values (2631,'ESA','Brazalete ESD/QR','Brazalete de Descarga Electrostática: Cable de dos partes con conector de liberación rápida. Una parte permanece siempre unida al chasis de la computadora con tornillos y la otra está unida al brazalete. Dispone de extremos permanentes adicionales.');
Insert into OE.PRODUCT_DESCRIPTIONS (PRODUCT_ID,LANGUAGE_ID,TRANSLATED_NAME,TRANSLATED_DESCRIPTION) values (2721,'ESA','Bolsa PC - L/S','Funda para la Computadora de Piel Negra: Capacidad para una sola computadora portátil con bolsillos para manuales, hardware adicional y documentación de trabajo. Bandas protectoras ajustables y bolsillo desmontable para la fuente de alimentación y los cables.');
Insert into OE.PRODUCT_DESCRIPTIONS (PRODUCT_ID,LANGUAGE_ID,TRANSLATED_NAME,TRANSLATED_DESCRIPTION) values (2722,'ESA','Bolsa PC - L/D','Funda para la Computadora de Piel Negra: Capacidad para dos computadoras portátiles con bolsillos para manuales, hardware adicional y documentación de trabajo. Bandas protectoras ajustables y bolsillos desmontable para las fuentes de alimentación y los cables. Asa de ancho doble para una mayor comodidad.');
Insert into OE.PRODUCT_DESCRIPTIONS (PRODUCT_ID,LANGUAGE_ID,TRANSLATED_NAME,TRANSLATED_DESCRIPTION) values (2725,'ESA','Lubricante','Lubricante para las puertas y los deslizadores de la unidad de CD-ROM. Boquilla ajustable de limpieza automática para flujo medio o de alta calidad.');
Insert into OE.PRODUCT_DESCRIPTIONS (PRODUCT_ID,LANGUAGE_ID,TRANSLATED_NAME,TRANSLATED_DESCRIPTION) values (2782,'ESA','Bolsa PC - C/S','Funda para la Computadora de Lona: Capacidad para una sola computadora portátil con bolsillos para manuales, hardware adicional y documentación de trabajo. Bandas protectoras ajustables y bolsillo desmontable para la fuente de alimentación y los cables. Bolsillo exterior con cierre con broche para un fácil acceso durante el viaje.');
