Insert into OE.PRODUCT_DESCRIPTIONS (PRODUCT_ID,LANGUAGE_ID,TRANSLATED_NAME,TRANSLATED_DESCRIPTION) values (3400,'PT','HD 8GB /SE','Unidade de disco rígido com capacidade para 8GB SCSI (externa). As unidades de disco Supra7 fornecem a tecnologia mais recente para melhorar o desempenho empresarial e aumentam a taxa máxima de transferência de dados até 255MB/s.');
