Insert into OE.PRODUCT_DESCRIPTIONS (PRODUCT_ID,LANGUAGE_ID,TRANSLATED_NAME,TRANSLATED_DESCRIPTION) values (1749,'PT','DIMM - 256MB','DIMM de Memória: RAM 256 MB. (SDRAM de 100-MHz Registada)');
