Insert into OE.PRODUCT_DESCRIPTIONS (PRODUCT_ID,LANGUAGE_ID,TRANSLATED_NAME,TRANSLATED_DESCRIPTION) values (1782,'PTB','Compact 400/DQ','Impressora de qualidade de carta de alta velocidade (400 caracteres por segundo). Dimensões (AxLxP): 12,37 x 27,96 x 23,92 polegadas. Interface: RS-232 serial (25 pinos), 3 slots de expansão. Tamanho do papel: A2, A3, A4.');
