Insert into OE.PRODUCT_DESCRIPTIONS (PRODUCT_ID,LANGUAGE_ID,TRANSLATED_NAME,TRANSLATED_DESCRIPTION) values (3060,'N','Skjerm 17/HR','CRT-skjerm 17 tommer (16 synlige) høy oppløsning. Glimrende bildeytelse og fordelen av ekstra skjermplass. Denne skjermen har en skarp og fargerik fremstilling til en utrolig pris. Med en mengde ledende funksjoner, inkludert visningskontroller på skjermen.');
