Insert into OE.PRODUCT_DESCRIPTIONS (PRODUCT_ID,LANGUAGE_ID,TRANSLATED_NAME,TRANSLATED_DESCRIPTION) values (2631,'N','ESD-bånd/QR','Elektrostatisk avledningsbånd: 2 deler med enkel frigjøringskontakt. Den ene delen er permanent montert på datamaskinen med en skru, den andre er knyttet til båndet. Ekstra permanente ender er tilgjengelig.');
