Insert into OE.PRODUCT_DESCRIPTIONS (PRODUCT_ID,LANGUAGE_ID,TRANSLATED_NAME,TRANSLATED_DESCRIPTION) values (1726,'PL','Monitor LCD 11/PM','11-calowy pasywny monitor z wyświetlaczem ciekłokrystalicznym. Płaski ekran o dużej rozdzielczości zapewnia doskonałą jakość obrazu ze zmniejszoną poświatą.');
Insert into OE.PRODUCT_DESCRIPTIONS (PRODUCT_ID,LANGUAGE_ID,TRANSLATED_NAME,TRANSLATED_DESCRIPTION) values (2359,'PL','Monitor LCD 9/PM','9-calowy pasywny monitor z wyświetlaczem ciekłokrystalicznym. Niewielkie wymiary monitora pozwalają zaoszczędzić wiele cennego miejsca na biurku. �?atwa konfiguracja dzięki zgodności ze standardem "plug and play".');
Insert into OE.PRODUCT_DESCRIPTIONS (PRODUCT_ID,LANGUAGE_ID,TRANSLATED_NAME,TRANSLATED_DESCRIPTION) values (3060,'PL','Monitor 17/HR','17-calowy (16-calowy obszar wyświetlania) monitor kineskopowy o dużej rozdzielczości. Doskonała jakość obrazu i korzyści wynikające z dużego ekranu. Zapewnia ostry obraz z wiernym odtwarzaniem barw. Jest wyposażony w najnowocześniejsze funkcje, w tym wyświetlane na ekranie menu sterujące.');
Insert into OE.PRODUCT_DESCRIPTIONS (PRODUCT_ID,LANGUAGE_ID,TRANSLATED_NAME,TRANSLATED_DESCRIPTION) values (2631,'PL','Uziemienie ESD Bracelet/QR','Przewód uziemiający: 2 odcinki z szybkozłączką. Jeden odcinek jest na stałe przykręcany do obudowy komputera. Dostępny dodatkowe mocowanie stałe.');
Insert into OE.PRODUCT_DESCRIPTIONS (PRODUCT_ID,LANGUAGE_ID,TRANSLATED_NAME,TRANSLATED_DESCRIPTION) values (2721,'PL','Torba na PC - L/S','Czarna torba skórzana na laptop - kieszenie na podręczniki, papier i dodatkowe urządzenia peryferyjne. Regulowane pasy i odpinana kieszeń na zasilacz i przewody.');
Insert into OE.PRODUCT_DESCRIPTIONS (PRODUCT_ID,LANGUAGE_ID,TRANSLATED_NAME,TRANSLATED_DESCRIPTION) values (2722,'PL','Torba na PC - L/D','Czarna torba skórzana na laptop - mieści dwa laptopy; kieszenie na podręczniki, papier i dodatkowe urządzenia peryferyjne. Regulowane, wygodne pasy o podwójnej szerokości i odpinana kieszeń na zasilacz i przewody.');
Insert into OE.PRODUCT_DESCRIPTIONS (PRODUCT_ID,LANGUAGE_ID,TRANSLATED_NAME,TRANSLATED_DESCRIPTION) values (2725,'PL','Smar','Smar do drzwiczek i prowadnic stacji CD-ROM-ów. Samoczyszcząca dysza o regulowanym przepływie (mały-średni).');
