Insert into OE.PRODUCT_DESCRIPTIONS (PRODUCT_ID,LANGUAGE_ID,TRANSLATED_NAME,TRANSLATED_DESCRIPTION) values (1940,'I','Cinturino ESD/Clip','Cinturino antistatico con clip che agevola la connessione allo chassis del computer o altri tipi di connessione.');
