Insert into OE.PRODUCT_DESCRIPTIONS (PRODUCT_ID,LANGUAGE_ID,TRANSLATED_NAME,TRANSLATED_DESCRIPTION) values (1755,'SF','32 Mt välimuisti /NM','32 megatavun peilaamaton välimuisti');
Insert into OE.PRODUCT_DESCRIPTIONS (PRODUCT_ID,LANGUAGE_ID,TRANSLATED_NAME,TRANSLATED_DESCRIPTION) values (2406,'SF','64 Mt välimuisti /M','64 megatavun peilattu välimuisti');
Insert into OE.PRODUCT_DESCRIPTIONS (PRODUCT_ID,LANGUAGE_ID,TRANSLATED_NAME,TRANSLATED_DESCRIPTION) values (2404,'SF','64 Mt välimuisti /NM','64 megatavun peilaamaton välimuisti FPM-muistipiirit on sovitettu 5 voltin SIMM-kammoille, mutta niitä saa myös 3,3 voltin DIMM-muistikampoina.');
Insert into OE.PRODUCT_DESCRIPTIONS (PRODUCT_ID,LANGUAGE_ID,TRANSLATED_NAME,TRANSLATED_DESCRIPTION) values (1770,'SF','8 Mt välimuisti /NM','8 megatavun peilaamaton välimuisti (100 MHz SDRAM)');
Insert into OE.PRODUCT_DESCRIPTIONS (PRODUCT_ID,LANGUAGE_ID,TRANSLATED_NAME,TRANSLATED_DESCRIPTION) values (2412,'SF','8 Mt EDO-muisti','8 megatavun 8x32 EDO SIMM-muisti. Extended Data Out -tyypin muisti eroaa FPM (Fast Page Mode) -muistista vähän, mutta silti merkittävästi. FPM-muistista poiketen EDO-muistin lähtevän datan ohjaimet pysyvät päällä, kun muistinohjaus poistaa sarakeosoitteen ennen uuden kierroksen aloittamista. Siksi uusi datakierros voi alkaa ennen kuin edellinen on päättynyt. EDO-muistia saadaan sekä 3,3- että 5-voltin SIMM- ja DIMM-kampoina.');
Insert into OE.PRODUCT_DESCRIPTIONS (PRODUCT_ID,LANGUAGE_ID,TRANSLATED_NAME,TRANSLATED_DESCRIPTION) values (2378,'SF','DIMM - 128 Mt','128 megatavun DIMM-muisti. SIMM-muisteja vaihdetaan yleensä DIMM-muisteiksi, koska jälkimmäiset tukevat 64-bittisten prosessorien leveämpiä väyliä. DIMM-muistit ovat 64- tai 72-bittisiä, kun taas SIMM-muistit ovat vain 32- tai 36-bittisiä (pariteettibitit mukaan lukien).');
Insert into OE.PRODUCT_DESCRIPTIONS (PRODUCT_ID,LANGUAGE_ID,TRANSLATED_NAME,TRANSLATED_DESCRIPTION) values (3087,'SF','DIMM - 16 Mt','16 megatavun Citrus OLX DIMM.');
Insert into OE.PRODUCT_DESCRIPTIONS (PRODUCT_ID,LANGUAGE_ID,TRANSLATED_NAME,TRANSLATED_DESCRIPTION) values (2384,'SF','DIMM - 1Gt','DIMM-muisti: 1 gigatavun RAM.');
Insert into OE.PRODUCT_DESCRIPTIONS (PRODUCT_ID,LANGUAGE_ID,TRANSLATED_NAME,TRANSLATED_DESCRIPTION) values (3054,'SF','Plasmanäyttö 10/XGA','10 tuuman vakiomallin plasmanäyttö XGA-näyttötarkkuudella. Tämän käytännössä täysin tasaisen, korkean näyttötarkkuuden näytön kuvanlaatu on erinomainen. Myös heijastuksia on vähennetty.');
Insert into OE.PRODUCT_DESCRIPTIONS (PRODUCT_ID,LANGUAGE_ID,TRANSLATED_NAME,TRANSLATED_DESCRIPTION) values (1750,'SF','DIMM - 2 Gt','DIMM-muisti: 2 gigatavun RAM.');
Insert into OE.PRODUCT_DESCRIPTIONS (PRODUCT_ID,LANGUAGE_ID,TRANSLATED_NAME,TRANSLATED_DESCRIPTION) values (2394,'SF','DIMM - 32 Mt','32 megatavun DIMM-muistipäivitys.');
Insert into OE.PRODUCT_DESCRIPTIONS (PRODUCT_ID,LANGUAGE_ID,TRANSLATED_NAME,TRANSLATED_DESCRIPTION) values (2400,'SF','DIMM - 512 Mt','512 megatavun DIMM-muisti. Muistipäivitys vähemmillä komponenteilla: DIMM-muistikampoja tarvitaan vähemmän järjestelmän muistinlisäykseen kuin jos käytettäisiin SIMM-muistia. Muistinlaajennuksen enimmäismäärä on kasvanut: Samoilla muistipaikoilla voidaan DIMM-muistia käyttämällä saada suurempi muistinlaajennus kuin SIMM-muisteilla. DIMM-kampojen molemmilla puolilla on omat kontaktinsa, jolloin niihin voidaan tallentaa kaksi kertaa enemmän tietoa kuin SIMM-kampoihin.');
Insert into OE.PRODUCT_DESCRIPTIONS (PRODUCT_ID,LANGUAGE_ID,TRANSLATED_NAME,TRANSLATED_DESCRIPTION) values (1763,'SF','DIMM - 64 Mt','DIMM-muisti: 64 Mt RAM (100 MHz Unregistered ECC SDRAM)');
Insert into OE.PRODUCT_DESCRIPTIONS (PRODUCT_ID,LANGUAGE_ID,TRANSLATED_NAME,TRANSLATED_DESCRIPTION) values (2396,'SF','EDO - 32 Mt','EDO-SIMM-muisti: 32 Mt RAM (100 MHz Unregistered ECC SDRAM). FPM-muistin tapaan EDO-muistia on saatavissa 3,3- ja 5-volttisina SIMM- ja DIMM-kampoina. Jos EDO-muistia asennetaan tietokoneeseen, joka ei sitä tue, muisti ei ehkä toimi.');
Insert into OE.PRODUCT_DESCRIPTIONS (PRODUCT_ID,LANGUAGE_ID,TRANSLATED_NAME,TRANSLATED_DESCRIPTION) values (2272,'SF','RAM -16 Mt','SIMM-muisti: 16 megatavun RAM.');
Insert into OE.PRODUCT_DESCRIPTIONS (PRODUCT_ID,LANGUAGE_ID,TRANSLATED_NAME,TRANSLATED_DESCRIPTION) values (2274,'SF','RAM - 32 Mt','SIMM-muisti: 32 megatavun RAM.');
Insert into OE.PRODUCT_DESCRIPTIONS (PRODUCT_ID,LANGUAGE_ID,TRANSLATED_NAME,TRANSLATED_DESCRIPTION) values (3090,'SF','RAM - 48 Mt','48 megatavun SIMM RAM.');
Insert into OE.PRODUCT_DESCRIPTIONS (PRODUCT_ID,LANGUAGE_ID,TRANSLATED_NAME,TRANSLATED_DESCRIPTION) values (1739,'SF','SDRAM - 128 Mt','128 megatavun SDRAM. SDRAM voi käsitellä tietoa jopa 100 megahertsin nopeudella, mikä on jopa neljä kertaa tavallista DRAM-muistia nopeammin. SDRAM-muistin täysi hyöty saavutetaan kuitenkin vain tietokoneissa, jotka on suunniteltu käyttämään kyseistä muistia. SDRAM-muistia on saatavana 3,3 ja 5 voltin DIMM-kampoina.');
Insert into OE.PRODUCT_DESCRIPTIONS (PRODUCT_ID,LANGUAGE_ID,TRANSLATED_NAME,TRANSLATED_DESCRIPTION) values (3359,'SF','SDRAM - 16 Mt','SDRAM-muistipäivitysmoduuli, 16 Mt. SDRAM eli Synchronous Dynamic Random Access Memory tuli markkinoille EDO-muistin jälkeen. SDRAM-muistin arkkitehtuuri ja toiminta perustuu perinteiselle DRAM-tekniikalle, mutta poikkeaa merkittävästi päämuistin käsittelyssä, mikä vähentää tiedonhakuaikoja entisestään. SDRAM on synkronoitu tietokoneen pääprosessoria ohjaavan kellon kanssa. Tämä tarkoittaa, että mikroprosessoria ohjaava järjestelmän kello ohjaa myös SDRAM:in toimintaa. Näin muistinohjain tietää, millä kellosyklillä tietopyyntö on valmiina, jolloin ajoitusviivet poistuvat.');
Insert into OE.PRODUCT_DESCRIPTIONS (PRODUCT_ID,LANGUAGE_ID,TRANSLATED_NAME,TRANSLATED_DESCRIPTION) values (3088,'SF','SDRAM - 32 Mt','32 megatavun SDRAM, jossa on ECC-virheenkorjaus. SDRAM-muistissa on useita muistipankkeja, jotka voivat toimia samanaikaisesti. Jatkuva tietovirta saavutetaan vaihtelemalla pankkien välillä.');
Insert into OE.PRODUCT_DESCRIPTIONS (PRODUCT_ID,LANGUAGE_ID,TRANSLATED_NAME,TRANSLATED_DESCRIPTION) values (2276,'SF','SDRAM - 48 Mt','SIMM-muisti: RAM - 48 Mt. SDRAM-muisti voi toimia pursketilassa. Pursketilassa yhtä dataosoitetta haettaessa kokonainen datalohko haetaan yhden asemesta. Oletetaan siis, että seuraava tarvittava tiedonpätkä on heti edellisen jälkeen. Koska yleensä näin on, ovat tiedot heti käytettävissä.');
Insert into OE.PRODUCT_DESCRIPTIONS (PRODUCT_ID,LANGUAGE_ID,TRANSLATED_NAME,TRANSLATED_DESCRIPTION) values (3086,'SF','VRAM - 16 Mt','16 megatavun Citrus Video-RAM. VRAM-muisti on tarkoitettu tietokoneen käsittelemän kuvatiedon tallentamiseen, eikä sitä käytetä muuhun. Muisti kehiteltiin mahdollistamaan jatkuva sarjamuotoisen tiedon kulku videonäyttöjen virkistysprosessia varten (kuvan piirtoa uudestaan näytölle).');
Insert into OE.PRODUCT_DESCRIPTIONS (PRODUCT_ID,LANGUAGE_ID,TRANSLATED_NAME,TRANSLATED_DESCRIPTION) values (3353,'SF','Kiintolevy 10Gt /R','10 gigatavun irrotettava kiintolevyasema. Supra7-levyasemat tarjoavat viimeisintä tekniikkaa yrityksen tehokkuuden parantamiseen, sillä ne nostavat tiedonsiirtonopeuden jopa 160 megatavuun sekunnissa.');
Insert into OE.PRODUCT_DESCRIPTIONS (PRODUCT_ID,LANGUAGE_ID,TRANSLATED_NAME,TRANSLATED_DESCRIPTION) values (1787,'SF','Prosessori D300','300 Mhz tuplaprosessori. Ainoastaan kevyeen henkilökohtaiseen käyttöön tai tiedostopalvelimeen, jota käyttää enintään 5 jatkuvaa käyttäjää. Tämä tuote poistuu todennäköisesti pian markkinoilta.');
Insert into OE.PRODUCT_DESCRIPTIONS (PRODUCT_ID,LANGUAGE_ID,TRANSLATED_NAME,TRANSLATED_DESCRIPTION) values (2439,'SF','Prosessori D400','400 Mhz tuplaprosessori. Hyvä hinnan ja suorituskyvyn suhde, keskikokoisiin LAN-tiedostopalvelimiin (enint. 100 jatkuvaa käyttäjää).');
Insert into OE.PRODUCT_DESCRIPTIONS (PRODUCT_ID,LANGUAGE_ID,TRANSLATED_NAME,TRANSLATED_DESCRIPTION) values (1788,'SF','Prosessori D600','600 Mhz tuplaprosessori. Huippuluokan nopea prosessori raskaaseen WAN-palvelinkäyttöön (jopa 200 jatkuvaa käyttäjää).');
Insert into OE.PRODUCT_DESCRIPTIONS (PRODUCT_ID,LANGUAGE_ID,TRANSLATED_NAME,TRANSLATED_DESCRIPTION) values (2375,'SF','Grafiikkaprosessori 1024x768','Grafiikkaprosessori 1024 x 768 näyttötarkkuudella. Erinomainen hinta/laatu-suhde 2D- ja 3D-sovelluksille SPINX 3.3- ja 4.0 -järjestelmissä. Yhteen korttin voidaan liittää kaksi näyttöä, jolloin katselumahdollisuudet tuplaantuvat. Kahdella 17 tuuman näytöllä päästään suurempaan kuva-alaan kuin yhdellä 21 tuuman näytöllä. Erinomainen vaihtoehto käyttäjille, jotka käyttävät monta sovellusta samanaikaisesti tai hakevat tietoa usein monista lähteistä.');
Insert into OE.PRODUCT_DESCRIPTIONS (PRODUCT_ID,LANGUAGE_ID,TRANSLATED_NAME,TRANSLATED_DESCRIPTION) values (2411,'SF','Grafiikkaprosessori 1280 x 1024','Grafiikkaprosessori 1280 x 1024 näyttötarkkuudella. Korkean tason 3D-suorituskykyä keskitason hintaan: 15 miljoonaa Gouraud-varjostettua kolmiota sekunnissa, optimoidut 3D-ohjaimet MCAD- ja DCC-sovelluksia varten ja lisäksi käyttäjän muokattavat asetukset. 64 megatavun DDR SDRAM muisti, jossa on yhdistetty kehyspuskuri, joka tukee true color -tilaa (16,7 miljoonaa väriä) kaikilla vakionäyttötarkkuuksilla.');
Insert into OE.PRODUCT_DESCRIPTIONS (PRODUCT_ID,LANGUAGE_ID,TRANSLATED_NAME,TRANSLATED_DESCRIPTION) values (1769,'SF','Grafiikkaprosessori 800 x 600','Grafiikkaprosessori 800 x 600 näyttötarkkuudella. Erinomainen ratkaisu mahtavia 2D-ominaisuuksia tai edistyneitä sovelluksia varten perus-3D-tukea etsiville. Suorituskyvyltään erinomainen laite hyvin vaativaan käyttöön; antaa käyttäjän keskittyä monimutkaisten mallien renderoinnin sijaan varsinaiseen suunnitteluun.');
