Insert into OE.PRODUCT_DESCRIPTIONS (PRODUCT_ID,LANGUAGE_ID,TRANSLATED_NAME,TRANSLATED_DESCRIPTION) values (1772,'PTB','HD 9.1GB @10000','Unidade de disco rígido - 9,1 GB, com até 10.000 RPM. Essas unidades destinam-se ao uso em ambientes empresariais de dados críticos. Facilidade para realizar negócios: você pode facilmente selecionar as unidades que precisa, independentemente da aplicação na qual elas serão distribuídas.');
Insert into OE.PRODUCT_DESCRIPTIONS (PRODUCT_ID,LANGUAGE_ID,TRANSLATED_NAME,TRANSLATED_DESCRIPTION) values (2414,'PTB','HD 9.1GB @10000 /I','Unidade de disco rígido 9,1 GB SCSI @ 10000 RPM (interna). As unidades de disco rígido Supra7 estão disponíveis com velocidade de eixo de 10.000 RPM e capacidades de 18GB e 9,1 GB. Interfaces SCSI e RS-232.');
Insert into OE.PRODUCT_DESCRIPTIONS (PRODUCT_ID,LANGUAGE_ID,TRANSLATED_NAME,TRANSLATED_DESCRIPTION) values (2415,'PTB','HD 9.1GB @7200','Unidade de disco rígido de 9,1 GB @ 7200 RPM. Kits de opções universais estão configurados e previamente montados na bandeja de conexão direta, apropriada para instalação imediata no servidor corporativo ou no sistema de armazenamento.');
