Insert into OE.PRODUCT_DESCRIPTIONS (PRODUCT_ID,LANGUAGE_ID,TRANSLATED_NAME,TRANSLATED_DESCRIPTION) values (1743,'RO','HD 18.2GB @10000 /E','Unitate disc dur extern - 18,2 GO, până la 10.000 RPM. Aceste unităţi sunt destinate cererii din mediile de întreprindere, cu date critice şi sunt ideale pentru utilizarea aplicaţiilor RAID.');
Insert into OE.PRODUCT_DESCRIPTIONS (PRODUCT_ID,LANGUAGE_ID,TRANSLATED_NAME,TRANSLATED_DESCRIPTION) values (2382,'RO','HD 18.2GB@10000 /I','Disc dur de 18,2 GO SCSI @ 10000 RPM (intern). Unităţile Supra7 Universal furnizează un nivel neegalat de protecţie a investiţiei şi simplicitate pentru clienţi, permiţând compatibilitatea unităţilor cu o mare varietate de platforme de întreprindere.');
Insert into OE.PRODUCT_DESCRIPTIONS (PRODUCT_ID,LANGUAGE_ID,TRANSLATED_NAME,TRANSLATED_DESCRIPTION) values (3399,'RO','HD 18GB /SE','Unitate externă disc dur de 18GO. Unităţile Supra5 Universal oferă posibilitatea conectării la cald pentru servere variate, tablouri RAID şi dispozitive de stocare din generaţii mai vechi.');
Insert into OE.PRODUCT_DESCRIPTIONS (PRODUCT_ID,LANGUAGE_ID,TRANSLATED_NAME,TRANSLATED_DESCRIPTION) values (3073,'RO','HD 6GB /I','Unitate disc dur de 6GO (internă). Unităţile Supra elimină riscul de incompatibilitate firmware.');
Insert into OE.PRODUCT_DESCRIPTIONS (PRODUCT_ID,LANGUAGE_ID,TRANSLATED_NAME,TRANSLATED_DESCRIPTION) values (1768,'RO','HD 8.2GB @5400','Disc dur - 8,2 GO, până la 5.400 RPM. Unităţile Supra elimină riscul de incompatibilitate firmware. Interfaţă serială standard RS-232.');
Insert into OE.PRODUCT_DESCRIPTIONS (PRODUCT_ID,LANGUAGE_ID,TRANSLATED_NAME,TRANSLATED_DESCRIPTION) values (2410,'RO','HD 8.4GB @5400','Disc dur de 8,4 GO @ 5400 RPM. Cost de deţinere redus: Unităţile pot fi utilizate pe diverse platforme de întreprindere. Această unitate cu conectare la cald îndeplineşte standarde riguroase de fiabilitate şi performanţă.');
Insert into OE.PRODUCT_DESCRIPTIONS (PRODUCT_ID,LANGUAGE_ID,TRANSLATED_NAME,TRANSLATED_DESCRIPTION) values (2257,'RO','HD 8GB /I','Unitate disc dur de 8GO (internă). Unităţile de disc Supra9 cu montare la cald furnizează posibilitatea instalării sau scoaterii discurilor în mod interactiv. Compatibilitate cu produse anterioare: Aveţi posibilitatea să combinaţi dispozitivele Supra2 şi Supra3 pentru soluţii optimizate şi dezvoltări viitoare.');
Insert into OE.PRODUCT_DESCRIPTIONS (PRODUCT_ID,LANGUAGE_ID,TRANSLATED_NAME,TRANSLATED_DESCRIPTION) values (3400,'RO','HD 8GB /SE','Unitate disc dur de 8GO, SCSI (externă). Unităţile Supra7 furnizează tehnologia de ultimă oră pentru îmbunătăţirea performanţelor la nivel de întreprindere, prin creşterea vitezei de transfer al datelor până la 255MO/s.');
