Insert into OE.PRODUCT_DESCRIPTIONS (PRODUCT_ID,LANGUAGE_ID,TRANSLATED_NAME,TRANSLATED_DESCRIPTION) values (2236,'RO','Monitor cu plasmă 10/TFT/XGA','Monitor TFT XGA, cu ecran plat de 10 inch pentru calculatoare laptop');
Insert into OE.PRODUCT_DESCRIPTIONS (PRODUCT_ID,LANGUAGE_ID,TRANSLATED_NAME,TRANSLATED_DESCRIPTION) values (3054,'RO','Monitor cu plasmă 10/XGA','Monitor cu plasmă, standard, 10 inch, rezoluţie XGA. Acest ecran virtual-plat, de înaltă rezoluţie, oferă o calitate remarcabilă a imaginii, cu reflexie redusă.');
Insert into OE.PRODUCT_DESCRIPTIONS (PRODUCT_ID,LANGUAGE_ID,TRANSLATED_NAME,TRANSLATED_DESCRIPTION) values (1782,'RO','Compact 400/DQ','Imprimantă de mare viteză, 400 caractere pe secundă, calitate schiţă. Dimensiuni (HxLxl): 17,34 x 24,26 x 26,32 inch. Interfaţă: RS-232 serială (9-pini), fără sloturi de extensie. Dimensiuni hârtie: A4, US Letter.');
Insert into OE.PRODUCT_DESCRIPTIONS (PRODUCT_ID,LANGUAGE_ID,TRANSLATED_NAME,TRANSLATED_DESCRIPTION) values (2430,'RO','Compact 400/LQ','Imprimantă de mare viteză, 400 caractere pe secundă, calitate literă. Dimensiuni (HxLxl): 12,37 x 27,96 x 23,92 inch. Interfaţă: RS-232 serială (25-pini), 3 sloturi de extensie. Dimensiuni hârtie: A2, A3, A4.');
Insert into OE.PRODUCT_DESCRIPTIONS (PRODUCT_ID,LANGUAGE_ID,TRANSLATED_NAME,TRANSLATED_DESCRIPTION) values (1792,'RO','Industrial 600/DQ','Imprimantă de mare viteză, 600 caractere pe secundă, color, calitate schiţă. Dimensiuni (HxLxl): 22,31 x 25,73 x 20,12 inch. Dimensiuni hârtie: de la 3x5 inch la 11x17 inch, format lat, depăşire integrală.');
Insert into OE.PRODUCT_DESCRIPTIONS (PRODUCT_ID,LANGUAGE_ID,TRANSLATED_NAME,TRANSLATED_DESCRIPTION) values (1791,'RO','Industrial 700/HD','Imprimantă matricială cu ace, 700 caractere pe secundă, rezistentă la condiţii dure de exploatare, cu protecţie la praf, pentru medii industriale. Interfaţă: Centronics paralelă, compatibilă IEEE 1284. Dimensiuni hârtie: de la 3x5 inch la 11x17 inch, format lat, depăşire integrală. Memorie: 4MO. Dimensiuni (HxLxl): 9,3 x 16,5 x 13 inch.');
Insert into OE.PRODUCT_DESCRIPTIONS (PRODUCT_ID,LANGUAGE_ID,TRANSLATED_NAME,TRANSLATED_DESCRIPTION) values (2302,'RO','Inkjet B/6','Imprimantă cu jet de cerneală, monocromă, 6 pagini/minut, rezoluţie 600x300 dpi. Interfaţă: paralelă Centronics, compatibilă IEEE 1284. Dimensiuni (HxLxl): 7,3 x 17,5 x 14 inch. Dimensiuni hârtie: A3, A4, legal S.U.A. Fără sloturi de extensie.');
Insert into OE.PRODUCT_DESCRIPTIONS (PRODUCT_ID,LANGUAGE_ID,TRANSLATED_NAME,TRANSLATED_DESCRIPTION) values (2453,'RO','Inkjet C/4','Imprimantă cu jet de cerneală, color (cu două cartuşe separate pentru cerneală), 6 pagini alb-negru pe minut, 4 pagini color pe minut, rezoluţie 600x300 dpi. Interfaţă: paralelă bidirecţională compatibilă IEEE 1284 şi serială (9-pini) RS-232 cu două 2 sloturi de extensie EIO. Memorie: 8MO, 96KO buffer de intrare.');
